#!/usr/bin/env bash
# set -x
set -euo pipefail

error_exit() {
    echo "$1" 1>&2
    exit 1
}

echo -e "Installing Extension"

echo -e "Checking out branch ${BRANCH} from ${REPOSITORY}"

mkdir /tmp/paymentgatewaycloud
curl -LJ "${REPOSITORY}/archive/${BRANCH}.tar.gz" | tar -xz --strip-components=1 --directory=/tmp/paymentgatewaycloud
cd /tmp/paymentgatewaycloud

if [ ! -z "${WHITELABEL}" ]; then
    echo -e "Running Whitelabel Script for ${WHITELABEL}"
    echo "y" | php build.php "gateway.mypaymentprovider.com" "${WHITELABEL}" || error_exit "Faled to Run Whitelabel Scriptfor '$WHITELABEL'"
    DEST_FILE="$(echo "y" | php build.php "gateway.mypaymentprovider.com" "${WHITELABEL}" | tail -n 1 | sed 's/.*Created file "\(.*\)".*/\1/g')" || error_exit "Faled to extract Zip File name"
    DB_FIELD_NAME="$WHITELABEL"
    #DB_FIELD_NAME="$(php -r "print strtoupper(str_replace(' ', '_', ucwords(preg_replace('/^a-z0-9]+/', ' ', '${WHITELABEL}'))));")" || error_exit "Failed to extract DB-Field Name"
    #unzip -q "${DEST_FILE}" -d /tmp/source || :
    php -r "\$zip = new ZipArchive; \$res = \$zip->open('$DEST_FILE');if (\$res === TRUE) {\$zip->extractTo('/tmp/source');\$zip->close();}"
    SRC_PATH="/tmp/source"
else
    SRC_PATH="/tmp/paymentgatewaycloud/src"
    DB_FIELD_NAME="PaymentGatewayCloud"
fi

mkdir -p "/opt/bitnami/opencart/catalog"
mkdir -p "/opt/bitnami/opencart/image/catalog"
mkdir -p "/opt/bitnami/opencart/system/library"
cp -rf $SRC_PATH/upload/admin/* /opt/bitnami/opencart/admin/
cp -rf $SRC_PATH/upload/catalog/* /opt/bitnami/opencart/catalog/
cp -rf $SRC_PATH/upload/image/catalog/* /opt/bitnami/opencart/image/catalog/
cp -rf $SRC_PATH/upload/system/library/* /opt/bitnami/opencart/system/library/

# Tell Opencart about the extension
OC_EXT_ID=$(mysql -B -h mariadb -u root bitnami_opencart -e "INSERT INTO \`oc_extension_install\` SET \`filename\` = 'opencart-${DB_FIELD_NAME,,}-${BRANCH}.ocmod.zip', \`extension_download_id\` = '0', \`date_added\` = NOW(); SELECT LAST_INSERT_ID();" | tail -n1) || error_exit "Failed to register Extension"
# Add all Extension files to Opencart DB
for file_path in $(find $SRC_PATH/upload -not -path '*/\.*' -printf "%P\n"); do
    mysql -B -h mariadb -u root bitnami_opencart -e "INSERT INTO \`oc_extension_path\` SET \`extension_install_id\` = '${OC_EXT_ID}', \`path\`  = '${file_path}', \`date_added\`  = NOW();"
done
# Register Extension
mysql -B -h mariadb -u root bitnami_opencart -e "INSERT INTO \`oc_modification\` SET \`extension_install_id\` = '${OC_EXT_ID}', \`name\` = '${DB_FIELD_NAME}', \`code\` = '$(echo "${DB_FIELD_NAME}" | sed '/^/{s/^//;s/\b\([[:alpha:]]\)\([[:alpha:]]*\)\b/\u\1\L\2/g;s/^//;}')', \`author\` = '${DB_FIELD_NAME}', \`version\` = '${BRANCH:0:7}', \`link\` = '', \`xml\` = '<modification>\n    <name>${DB_FIELD_NAME}</name>\n    <version>${BRANCH:0:7}</version>\n    <author>${DB_FIELD_NAME}</author>\n    <code>$(echo "${DB_FIELD_NAME}" | sed '/^/{s/^//;s/\b\([[:alpha:]]\)\([[:alpha:]]*\)\b/\u\1\L\2/g;s/^//;}')</code>\n</modification>', \`status\` = '1', \`date_added\` = NOW();"

echo -e "Configuration"

DB_FIELD_NAME=${DB_FIELD_NAME,,}

# Enable Extension
mysql -B -h mariadb -u root bitnami_opencart -e "INSERT INTO \`oc_extension\` (type, code) VALUES ('payment','${DB_FIELD_NAME}_creditcard');"

if [ $HTTPS ]; then
    # Enable HTTPS
    mysql -B -h mariadb -u root bitnami_opencart -e "INSERT INTO oc_setting SET store_id = '0', \`code\` = 'config', \`key\` = 'config_secure', \`value\` = '1', serialized = '0';"
else
    # Disable HTTPS
    mysql -B -h mariadb -u root bitnami_opencart -e "INSERT INTO oc_setting SET store_id = '0', \`code\` = 'config', \`key\` = 'config_secure', \`value\` = '0', serialized = '0';"
fi

ESCAPED_PASS=$(echo -n "${SHOP_PGC_PASSWORD}" | sed 's/&/\&amp;/g; s/</\&lt;/g; s/>/\&gt;/g; s/"/\&quot;/g; s/'"'"'/\&#39;/g')

